class Solution(object):
    def longestValidParentheses(self, s):
        """
        :type s: str
        :rtype: int
        """
        if len(s) <= 1: return 0

        dp = [0 for i in range(len(s))]
        for i in range(1, len(s)):
            if s[i] == '(': continue
            if s[i - 1] == '(':
                if i - 2 >= 0: dp[i] = dp[i - 2] + 2
                else:
                    dp[i] = 2
            else:
                if i - dp[i - 1] - 1 >= 0 and s[i - dp[i - 1] - 1] == '(':
                    if i - dp[i - 1] - 2 >= 0:
                        dp[i] = dp[i - 1] + dp[i - dp[i - 1] - 2] + 2
                    else:
                        dp[i] = dp[i - 1] + 2
        return max(dp)



